<template>
	<div class="payAndScore">
		<el-dialog
		  :visible.sync="dialogVisible" class="state_preview" :modal-append-to-body="true" :close-on-click-modal="false" @close="closeDialog">
			<div class="img_code" v-if="isPay">
				<p class="code_tit mb20 mt15">信息已确认，您需支付 <span>{{payData.payMoney}}</span> 元</p>
				<!-- <img src="~assets/img/code.png" alt=""> -->
				<qrcode v-if="qrcodeVal" :val="qrcodeVal" :size="200"></qrcode>
				<img v-if="!qrcodeVal" width="200px" class="mt" src="~assets/img/reg_login/Spinner.gif" alt="">
				<p class="logo_img mt20"><img src="~assets/img/reg_login/WeChat.png" alt="">微信支付</p>
				<div class="wechat">
		  <a :href="cartUrl+'/shop/pages/pay.html?order_sn='+ orderSn" target="blank" ref="otherPay">
			<span class="clre">———</span>
			<span>选择其他支付方式</span>
			<span class="clre">———</span>
		  </a>
		</div>
			</div>

			<div class="img_score" v-if="isScore">
				<p class="score_tit"><i class="el-icon-circle-check"></i>支付完成</p>
				<p class="evaluation_txt mt20 mb15">请为本次服务评个分吧！</p>
				<div class="rate_list">
					<span>专业能力:</span>
					<el-rate class="score_rate" v-model="ability">
					</el-rate>
				</div>
				<div class="rate_list">
					<span>服务态度:</span>
					<el-rate class="score_rate" v-model="attitude">
					</el-rate>
				</div>
				<div class="rate_list">
					<span>响应速度:</span>
					<el-rate class="score_rate" v-model="speed">
					</el-rate>
				</div>
				<div class="evaluation_txt mt20">
					<el-input class="textarea"
					  type="textarea"
					  :rows="3"
					  placeholder="其它想说的（匿名告诉服务商吧）"
					  v-model="textarea" :maxlength="200">
					</el-input>
				</div>
		<div class="score_btn mt20">
			<button class="btn_left" @click="closeDialog">下次再说</button>
			<button class="btn_right ml5" @click="scoreSubmit">匿名提交</button>
		</div>
			</div>
		</el-dialog>
	</div>
</template>
<script>
	import Vue from 'vue'
	import qs from 'qs'
	import qrcode from 'components/qrcode/qrcode.vue'
	const paymentUrl = CONFIG.payUrl +'/iprp_app/api/payment';
	const topayUrl = CONFIG.userUrl +'/payweb/topay.htm';
	const payStatusUrl = CONFIG.userUrl + '/payweb/paymentstatus.htm'
	const scoreSubmitUrl = './api/addWorkComment';
	const wxTag = 'wx_pub_qr';
	export default {
	components: {
		qrcode
	},
	props:['orderSn'],
	data() {
	  return {
		qrcodeVal:'', //二维码信息
		cartUrl: CONFIG.cartUrl,
		payData:{},
		timer:'',
		temp:1, //计数
		dialogVisible: true,
		isPay:true,
		isScore:false,
		ability:null,
		attitude:null,
		speed:null,
		textarea:''
	  }
	},
	methods: {
		//显示评分
		showDialogScore(){
			clearInterval(this.timer);
			this.isScore = true;
		this.isPay = false;
		this.$emit('refresh');
		},
		//评分提交，提示、关闭、刷新
		scoreSubmit(){
			if (!this.ability || !this.attitude || !this.speed) {
				this.$message.error('评分不能为空');
				return;
			}
			var ret = {
				professionalAbility: this.ability,
				serviceAttitude: this.attitude,
				responseSpeed: this.speed,
				advice: this.textarea,
				order_fictitious_sn: this.orderSn
			}
			this.$http.post(scoreSubmitUrl, ret)
			.then((resp) => {
				this.$message.success('提交成功！');
				this.closeDialog();
		  this.$emit('refresh');
		})
		.catch((error) => {
			console.log(error)
		});
		},
		//是否可使用微信支付
		wxPayIsEnable(paymentsList){
			var ret = false;
			paymentsList.forEach((item, i) => {
				if (item.code == 'wx_pub_qr') {
					ret = true;
				}
			})
			return ret;
		},
		//获取订单支付状态,成功后关闭当前，显示评分，刷新
		getPayStatus(){
			this.$http.post(payStatusUrl, qs.stringify({
				order_sn: this.orderSn
			})).then((resp) => {
				var data = resp.data;
		  if(data.code == 'success'){
			var paymentState = data.data.payment_state; 
			if (paymentState === 1) {
				this.temp = 1;
				this.showDialogScore();
			}
		  }
		})
		.catch((error) => {
			console.log(error)
		});
		},
		//获取支付参数
		getpay(orderSn){
			this.$http.post(topayUrl, qs.stringify({
				order_sn: orderSn
			})).then((resp) => {
				var data = resp.data;
				if (data.code == 'success') {
					this.payData = data.data;
					var paymentsList = data.data.paymentsList;
					if (this.wxPayIsEnable(paymentsList)) {
						this.getPayment(data.data);
					}else{
						//若不支持微信支付，自动跳转到支付平台
						this.closeDialog();
						this.$refs.otherPay.click();
					}
				}else{
					this.$message.error(data.msg)
				}
		})
		.catch((error) => {
			console.log(error)
		});
		},
		//获取微信二维码链接信息
		getPayment(data){
			var ret = {
				body: encodeURIComponent(data.body),
				channel: wxTag,
				client_ip: data.client_ip,
				order_sn: data.order_sn,
				payment_sn: data.payment_sn,
				sign: data.sign,
				subject: encodeURIComponent(data.subject),
				success_url: this.cartUrl+ '/shop/pages/paySuccess.html?order_sn='+ this.orderSn,
				timestamp: data.timestamp
			}
			this.$http.post(paymentUrl, ret)
			.then((resp) => {
				var data = resp.data;
				if(data.body) {
					this.qrcodeVal = data.body;
				}
		})
		.catch((error) => {
			console.log(error)
		});
		},
		closeDialog(){
			this.$emit('update:isToPay', false);
			this.timer && clearInterval(this.timer);
		},
	},
	mounted() {
		var self = this;
		self.getpay(this.orderSn);
		self.temp = 1;
		self.timer = setInterval(function() {
			self.temp ++;
			if (self.temp>300) {
				clearInterval(self.timer)
			}
			self.getPayStatus();
		},1000);
	},
	watch:{
	}
  }
</script>
